    unlocked_account = '0x21a31Ee1afC51d94C2eFcCAa2092aD1028285549' # Unlocked account with DAI and ETH
    my_account = str(accounts[0])

    print(f"balance big user before is: {contract_instance_dai.functions.balanceOf(unlocked_account).call()}")

    tx_hash = contract_instance_dai.functions.transfer(my_account, 10000).transact({'from': unlocked_account})
